INSERT OR IGNORE INTO perspectives (article_id, type, title, content) VALUES 
(1, 'economic', 'Цена войны: Экономический крах', 'Потеря серебряных рудников в Иберии нанесла Карфагену более сильный удар, чем разгром при Заме. Римская блокада фактически задушила морскую торговлю — основу пунической экономики.');

INSERT OR IGNORE INTO perspectives (article_id, type, title, content) VALUES 
(1, 'opponent', 'Взгляд из Карфагена', 'Для Ганнибала это была не война на уничтожение Рима, а попытка принудить его к новому договору. Он не хотел разрушать город, он хотел сломать политическую волю Сената.');
